	}
   
   public static function processCreate ($name, $description )
	{
		$is_duplicated = self::isNameDuplicated ( $name );
		
		try {
			
			if($is_duplicated)
				return false;
				
			if(is_null($name) || empty($name))
				return false;
				
			$_nw = new Category();   
			$_nw->name = trim($name);   
			$_nw->description = trim($description); 
			$_nw->save(); 
			
			return true;
			
		} catch ( Exception $e ) {			
			return false; 
		}	 
		  
	}

	public static function processUpdate ($_id, $name, $description )
	{
		$q = Doctrine_Query::create( )
			->update('Category cat')
			->set('cat.name', '?', trim($name))  
			->set('cat.description', '?', trim($description))  
			->where('cat.id = ?', $_id)
			->execute();	

		return ( $q > 0 );   
	}
   
   public static function processDelete ( $_id ) 
   {
		$q = Doctrine_Query::create( )
			->delete ('Category cat')
			->where('cat.id=?', $_id)
			->execute ( );	
		return ( $q	> 0  );  
	}
	
	public static function processObject ( $_id ) 
	{
		$q = Doctrine_Query::create( )
							->select("cat.*, cat.name as categoryName")
							->from("Category cat") 
							->where("cat.id=?", $_id)
							->fetchOne ( );
		return ( ! $q ? null : $q ); 
	}
	
	public static function findWtithName ( $name ) 
	{
		$q = Doctrine_Query::create( )
							->select("cat.*")
							->from("Category cat") 
							->where("cat.name LIKE ?", trim($name))
							->fetchOne ( );
							
		return ( count($q) <= 0 ? null : $q); 
	}
    
	public static function processSelection ( $offset=0, $limit=100 ) 
	{
		$q = Doctrine_Query::create( )
							->select("cat.*, cat.name as categoryName")
							->from("Category cat") 
							->offset($offset)
							->limit($limit)
							->execute ( );
		return ( count($q) <= 0 ? null : $q ); 
	}
