				//->leftJoin("grp.groupModulePermissions per on per.group_id = grp.id")
				->offset($offset)
				->limit($limit)
				//->where('prt.id  != ?', $customer_id)
				->execute( ); 

		return ( count ( $q ) <= 0 ? null : $q ); 
		
	}
}
